sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {

    initialize: function() {
    },

	isDatePast: function(strDate){
		var rightnow = new GlideDateTime();
		var week_of_the_year = rightnow.getWeekOfYearLocalTime();
		gs.info("Week of the year : " + week_of_the_year);

		var int_week_of_the_year = parseInt(week_of_the_year);
		gs.info("Integer Week of the year : " + int_week_of_the_year);

		var testdate = new GlideDateTime(strDate);
		if (testdate.before(rightnow))
			return true;
		else
			return false;
	},

	isToday: function(strDate){
		var rightnow = new GlideDateTime();
		var whendate = new GlideDateTime(strDate);

		var today = rightnow.getLocalDate();
		var whenday = whendate.getLocalDate();
		if (today.compareTo(whenday) == 0)
			return true;
		else
			return false;
	},

    type: 'NeedItUtils'
};
